import React from "react";
import axios from "axios";
import cheerio from "cheerio";

type Props = {
  children: React.ReactNode;
};

export const DataContext = React.createContext(undefined);

const getData = async () => {
  const url = "https://podzial.mech.pk.edu.pl/stacjonarne/html/plany/o30.html";

  const response = await fetch(url);
  const body = await response.text();

  let $ = cheerio.load(body);

  const data = [""];

  $("td.l").each((i, element) => {
    element.children.forEach((x) => {
      // console.log(x.type, Object.getOwnPropertyNames(x))
      if (x.type === "tag") {
        if (x.children[0]) {
          if (x.children[0].type === "tag") {
            if (x.children[0].children[0].type === "text") {
              // console.log("----")
              // console.log(x.children[0].children[0].data)
              data.push(x.children[0].children[0].data)
              // console.log("----")
            }
            
          } else {
            // console.log("tag cos innego")
            console.log(x.children[0].type)
          }
        } else {
          // console.log("ds")
          data.push("pusta komorka")
        }
        
        // if (x.children[0].type === "text") {
        //   console.log(x.children[0].data);
        // } else {
        //   console.log(x.children[0]);
        // }
      } else if (x.type === "text") {
        data.push(x.data)
        if (x.data.trim() === "") {
          // console.log("puste")
        } else {
          // console.log("niepuste")
        }
        // console.log(x.data);
      }
    });
  });
};

const Scrapper: React.FC<Props> = ({ children }) => {
  const data: any = [];
  axios
    .get("https://podzial.mech.pk.edu.pl/")
    .then((urlResponse) => {
      const $ = cheerio.load(urlResponse.data);

      getData();

      $("div").each((i, element) => {
        data.push(element);
      });
    })
    .catch((e) => console.log(e));
  return <DataContext.Provider value={data}>{children}</DataContext.Provider>;
};

export default Scrapper;
