import { useReducer } from "react";
import Items from "./Items";
import "./nav.css";

export type Actions =
  | { type: "WEEK"; newGroup: "N" | "P" }
  | { type: "K"; newGroup: "12K1" | "12K2" }
  | { type: "GL"; newGroup: "GL01" | "GL02" | "GL03" | "GL04" }
  | { type: "GK"; newGroup: "GK01" | "GK02" | "GK03" }
  | { type: "GP"; newGroup: "GP01" | "GP02" | "GP03" };

export type CurrentGroups = {
  week: string;
  k: string;
  gl: string;
  gk: string;
  gp: string;
};

const Nav: React.FC = () => {
  const starterGroups = {
    week: "N",
    k: "12K1",
    gl: "L01",
    gk: "K01",
    gp: "P01",
  };

  const reducer = (state: CurrentGroups, action: Actions) => {
    switch (action.type) {
      case "WEEK":
        return { ...state, week: action.newGroup };
      case "K":
        return { ...state, k: action.newGroup };
      case "GL":
        return { ...state, gl: action.newGroup };
      case "GK":
        return { ...state, gk: action.newGroup };
      case "GP":
        return { ...state, gp: action.newGroup };
      default:
        return state;
    }
  };

  const [currentGropus, currentGroupsDispatch] = useReducer(reducer, {
    week: starterGroups.week,
    k: starterGroups.k,
    gl: starterGroups.gl,
    gk: starterGroups.gk,
    gp: starterGroups.gp,
  });

  return (
    <nav className="nav">
      <ul className="ul">
        <Items
          groups={["N", "P"]}
          currentGroups={currentGropus}
          currentGroupsDispatch={currentGroupsDispatch}
        />
        <Items
          groups={["12K1", "12K2"]}
          currentGroups={currentGropus}
          currentGroupsDispatch={currentGroupsDispatch}
        />
        <Items
          groups={["L01", "L02", "L03", "L04"]}
          currentGroups={currentGropus}
          currentGroupsDispatch={currentGroupsDispatch}
        />
        <Items
          groups={["K01", "K02", "K03"]}
          currentGroups={currentGropus}
          currentGroupsDispatch={currentGroupsDispatch}
        />
        <Items
          groups={["PO1", "PO2", "P03"]}
          currentGroups={currentGropus}
          currentGroupsDispatch={currentGroupsDispatch}
        />
      </ul>
    </nav>
  );
};

export default Nav;
