import "./template.css";

type Props = {
  rows: number;
  columns: number;
};

const Template: React.FC<Props> = ({ rows, columns }) => {
  const grid = rows * columns;

  const date = new Date("December 14, 2026 07:30:00");
  let hoursAndMinutes = date.getHours() + ":" + date.getMinutes();

  function addHours(hours = 1) {  
    date.setTime(date.getTime() + (hours*60*60*1000));
    return date.getHours() + ":" + date.getMinutes();
  }

  function addMinutes(numOfMinutes, date = new Date()) {
    date.setMinutes(date.getMinutes() + numOfMinutes);
  
    return date;
  }

  return (
    <div className="template">
      {[...Array(grid)].map((e, i) => {
        if (i % 6 === 0) {
          hoursAndMinutes = addHours();
        }
        return i % columns === 0 ? <div key={i} className="cell">{hoursAndMinutes}</div> : <div key={i} className="cell"></div>
      })}
    </div>
  );
};

export default Template;
