import React, { useEffect } from "react";
import axios from "axios";
import cheerio from "cheerio";

type Props = {
  children: React.ReactNode;
};

export const DataContext = React.createContext<
  Promise<string[]> | string[] | undefined
>(undefined);

export const getData = async () => {
  const url = "https://podzial.mech.pk.edu.pl/stacjonarne/html/plany/o30.html";

  const response = await fetch(url);
  const body = await response.text();

  let $ = cheerio.load(body);

  const data = [""];

  $("td.l").each((i, element) => {
    element.children.forEach((x) => {
      console.log(i, x);
      // console.log(x.type, Object.getOwnPropertyNames(x))
      if (x.type === "tag") {
        if (x.children[0]) {
          if (x.children[0].type === "tag") {
            if (x.children[0].children[0].type === "text") {
              // console.log("----")
              // console.log(x.children[0].children[0].data)
              data.push(x.children[0].children[0].data);
              // console.log("----")
            }
          } else if (x.children[0].type === "text") {
            // console.log("tag cos innego")
            if (x.children[0].data.includes("#")) return;
            data.push(x.children[0].data);
            // console.log(x.children[0].data)
          }
        } else {
          // console.log("ds")
          data.push("pusta komorka");
        }
        // console.log("tag", x.children[0])
        // if (x.children[0].type === "text") {
        //   console.log(x.children[0].data);
        // } else {
        //   console.log(x.children[0]);
        // }
      } else if (x.type === "text") {
        console.log(x.next, x.data.trim())
        console.log(x.next === null, x.data.trim() === "")
        if (x.next === null && x.data.trim() === "") {
          data.push("pusta komorka!");
        } else if (x.data.trim() === "") {
          data.push("puste lub zajecia dodatkowe");
        } else {
          data.push(x.data);
        }
        // console.log(x.data);
      }
    });
  });
  return data;
};

const Scrapper = async () => {
  return await getData();
};

export default Scrapper;
