import { useReducer } from "react";
import "./template.css";

type Props = {
  rows: number;
  columns: number;
  daysOfWeek: string[];
};

const Template: React.FC<Props> = ({ rows, columns, daysOfWeek }) => {
  const grid = rows * columns;

  const date = new Date("December 14, 2026 06:30:00");

  type Time = {
    date: Date;
    hour: string;
  };
  type Actions =
    | { type: "addHours"; hours: number }
    | { type: "addMinutes"; minutes: number };

  const reducer = (state: Time, action: Actions): Time => {
    let date: Date;
    switch (action.type) {
      case "addHours":
        date = new Date(
          state.date.setTime(
            state.date.getTime() + action.hours * 60 * 60 * 1000
          )
        );
        return {
          date: date,
          hour: date.getHours() + ":" + date.getMinutes(),
        };

      case "addMinutes":
        date = new Date(
          state.date.setMinutes(state.date.getMinutes() + action.minutes)
        );
        return {
          date: date,
          hour: date.getHours() + ":" + date.getMinutes(),
        };
      default:
        return state;
    }
  };

  const [time, timeDispatch] = useReducer(reducer, {
    date: new Date("December 14, 2026 06:30:00"),
    hour: date.getHours() + ":" + date.getMinutes(),
  });

  timeDispatch()

  let hoursAndMinutes = date.getHours() + ":" + date.getMinutes();

  function addHours(hours = 1) {
    date.setTime(date.getTime() + hours * 60 * 60 * 1000);
    return date.getHours() + ":" + date.getMinutes();
  }

  function addMinutes(minutes = 45) {
    date.setMinutes(date.getMinutes() + minutes);

    return date.getHours() + ":" + date.getMinutes();
  }

  function addRemaining() {
    let hoursAndMinutesList = hoursAndMinutes.split(":");
    hoursAndMinutesList = hoursAndMinutesList.map((element, index) => {
      if (element.length === 1 && index === 0) {
        return "0" + element;
      } else if (element.length === 1 && index === 1) {
        return element + "0";
      } else return element;
    });
    return hoursAndMinutesList.join(":");
  }

  return (
    <div className="template">
      {[...Array(grid)].map((e, i) => {
        if (i === 0) {
          return <div key={i} className="cell"></div>;
        }

        if (i < columns) {
          return (
            <div key={i} className="cell">
              {daysOfWeek[i - 1]}
            </div>
          );
        }

        if ((i % columns) * 2 === 0) {
          hoursAndMinutes = addMinutes();
          hoursAndMinutes = addRemaining();
        } else if (i % columns === 0) {
          hoursAndMinutes = addHours();
          hoursAndMinutes = addRemaining();
        }

        return i % columns === 0 ? (
          <div key={i} className="cell">
            {hoursAndMinutes}
          </div>
        ) : (
          <div key={i} className="cell"></div>
        );
      })}
    </div>
  );
};

export default Template;
