import { useReducer } from "react";
import Items from "./Items";
import { Actions, CurrentGroups } from "./module";
import "./nav.css";

const Nav: React.FC = () => {
  const groups = {
    week: ["N", "P"],
    k: ["12K1", "12K2"],
    gl: ["L01", "L02", "L03", "L04"],
    gk: ["K01", "K02", "K03"],
    gp: ["P01", "P02", "P03"],
  };

  const reducer = (state: CurrentGroups, action: Actions) => {
    switch (action.type) {
      case "WEEK":
        return { ...state, week: action.newGroup };
      case "K":
        return { ...state, k: action.newGroup };
      case "GL":
        return { ...state, gl: action.newGroup };
      case "GK":
        return { ...state, gk: action.newGroup };
      case "GP":
        return { ...state, gp: action.newGroup };
      default:
        return state;
    }
  };

  const [currentGropus, currentGroupsDispatch] = useReducer(reducer, {
    week: groups.week[0],
    k: groups.k[0],
    gl: groups.gl[0],
    gk: groups.gk[0],
    gp: groups.gp[0],
  });

  return (
    <nav className="nav">
      <ul className="ul">
        <Items
          type="WEEK"
          groups={groups.week}
          currentGroups={currentGropus}
          currentGroupsDispatch={currentGroupsDispatch}
        />
        <Items
          type="K"
          groups={groups.k}
          currentGroups={currentGropus}
          currentGroupsDispatch={currentGroupsDispatch}
        />
        <Items
          type="GL"
          groups={groups.gl}
          currentGroups={currentGropus}
          currentGroupsDispatch={currentGroupsDispatch}
        />
        <Items
          type="GK"
          groups={groups.gk}
          currentGroups={currentGropus}
          currentGroupsDispatch={currentGroupsDispatch}
        />
        <Items
          type="GP"
          groups={groups.gp}
          currentGroups={currentGropus}
          currentGroupsDispatch={currentGroupsDispatch}
        />
      </ul>
    </nav>
  );
};

export default Nav;
