import { useReducer } from "react";
import Items from "./Items";
import "./nav.css";

type Actions = | { type: "WEEK"; newGroup: "N" | "P" } | { type: "K"; newGroup: "12K1" | "12K2" } | { type: "GL"; newGroup: "GL01" | "GL02" | "GL03" | "GL04" } | { type: "GK"; newGroup: "GK01" | "GK02" | "GK03" } | { type: "GP"; newGroup: "GP01" | "GP02" | "GP03" }

type CurrentGroups = {
  week: string;
    k: string;
    gl: string;
    gk: string;
    gp: string;
}

const Nav: React.FC = () => {
  const reducer = (state: CurrentGroups, action: Actions) => {
    switch(action.type) {
      case "WEEK": return { ...state, week: action.newGroup }
      case "K": return { ...state, k: action.newGroup }
      case "GL": return { ...state, gl: action.newGroup }
      case "GK": return { ...state, k: action.newGroup }
      case "GP": return { ...state, k: action.newGroup }
    }
  }

  const [currentGropus, currentGroupsDispatch] = useReducer(reducer, {
    week: "N",
    k: "12K2",
    gl: "L04",
    gk: "K02",
    gp: "P02"
  })

  return (
    <nav className="nav">
      <ul className="ul">
        <Items groups={["N", "P"]} />
        <Items groups={["12K1", "12K2"]} />
        <Items groups={["L01", "L02", "L03", "L04"]} />
        <Items groups={["K01", "K02", "K03"]} />
        <Items groups={["PO1", "PO2", "P03"]} />
      </ul>
    </nav>
  );
}

export default Nav;
